// html2pdf.d.ts

declare module 'html2pdf.js' {
    export interface Html2PdfOptions {
      margin?: number | [number, number, number, number]; // Margin for the PDF
      filename?: string; // The name of the generated PDF file
      image?: { type: string; quality: number }; // Image settings for the PDF
      html2canvas?: { scale: number }; // HTML2Canvas settings (used for rendering HTML to canvas)
      jsPDF?: { unit: string; format: string; orientation: string }; // jsPDF settings (used for PDF generation)
    }
  
    export default function html2pdf(
      element: HTMLElement | string, // HTML element or string selector to convert into PDF
      options?: Html2PdfOptions // Options for customizing the PDF generation
    ): Promise<void>;
  }
  